///////////OWLCAROUSEL////////////
$(document).ready(function () {
    jQuery('.slidershow').owlCarousel({
        loop: true,
        nav: false,
        dots: false,
        animateOut: 'fadeOut',
        autoplay: true,
        responsive: {
            0: {
                items: 1
            },
            600: {
                items: 1
            },
            1000: {
                items: 1
            }
        }
    });

    jQuery('.regent-slide').owlCarousel({
        loop: true,
        nav: false,
        dots: false,
        animateOut: 'fadeOut',
        autoplay: true,
        responsive: {
            0: {
                items: 1
            },
            600: {
                items: 1
            },
            1000: {
                items: 3
            }
        }
    });

    jQuery('.client-slide').owlCarousel({
        loop: true,
        nav: false,
        dots: false,
        animateOut: 'fadeOut',
        autoplay: true,
        responsive: {
            0: {
                items: 2
            },
            600: {
                items: 3
            },
            1000: {
                items: 9
            }
        }
    });

    $('#loginForm').submit(function (e) {
        e.preventDefault();
        var data = $(this).serialize();
        var token = $("input[name='_token']").val();
        $.ajax({
            type: 'POST',
            url: '/login',
            data: {_token: token, data: data},
            success: function (respone) {
                $('#loading-screen').hide();
                $('#successModal').modal('show');
            }
        });
    });
    $(document).ajaxStart(function () {
        $('#loginModal').hide();
        $('#loading-screen').show();
    });
    $('#requestForm').submit(function (e) {
        e.preventDefault();
        var data = $(this).serialize();
        var token = $("input[name='_token']").val();
        $.ajax({
            type: 'POST',
            url: '/register',
            data: {_token: token, data: data},
            success: function (respone) {
                $('#loading-screen').hide();
                $('#successModal').modal('show');
            }
        });
        return false;
    });
    $(document).ajaxStart(function () {
        $('#requestModal').hide();
        $('#loading-screen').show();
    });

    jQuery('.popover').popover({});

});

